"use strict";(self.webpackChunkalgosphere=self.webpackChunkalgosphere||[]).push([[948],{948:e=>{e.exports=JSON.parse('{"title":"Responsive Web Design","slug":"responsive-web-design","description":"Understand how to design responsive websites for multiple devices.","level":"Intermediate","chapters":[{"title":"Overview","slug":"","description":"This course teaches the principles of responsive web design, focusing on creating websites that adapt seamlessly across different screen sizes and devices. You will learn techniques such as fluid grids, media queries, and flexible layouts to ensure that your websites look great on desktops, tablets, and smartphones. By the end of the course, you will be able to build fully responsive, mobile-first websites."},{"title":"Introduction to Responsive Web Design","slug":"introduction","description":"Learn the importance of responsive design and its principles.","lessons":[{"title":"What is Responsive Design?","duration":"12 min"},{"title":"The Mobile-First Approach","duration":"15 min"},{"title":"Fluid Grids and Flexible Layouts","duration":"20 min"}]},{"title":"Media Queries","slug":"media-queries","description":"Learn how to use media queries to create responsive layouts.","lessons":[{"title":"Introduction to Media Queries","duration":"18 min"},{"title":"Breakpoints for Different Devices","duration":"15 min"},{"title":"Using max-width and min-width","duration":"12 min"},{"title":"Orientation and Aspect Ratio Queries","duration":"15 min"}]},{"title":"Responsive Images and Media","slug":"responsive-images-and-media","description":"Optimize images and media for different screen sizes.","lessons":[{"title":"Using the <picture> Element","duration":"12 min"},{"title":"srcset and Sizes Attributes","duration":"15 min"},{"title":"Optimizing Videos for Mobile","duration":"18 min"}]},{"title":"CSS Flexbox for Responsive Layouts","slug":"css-flexbox","description":"Master the use of Flexbox for creating flexible layouts.","lessons":[{"title":"Flexbox Basics","duration":"20 min"},{"title":"Aligning Items with Flexbox","duration":"18 min"},{"title":"Creating Responsive Navigation Menus","duration":"25 min"}]},{"title":"CSS Grid for Complex Layouts","slug":"css-grid","description":"Learn how to build advanced layouts using CSS Grid.","lessons":[{"title":"CSS Grid Fundamentals","duration":"22 min"},{"title":"Creating Two-Column and Three-Column Layouts","duration":"20 min"},{"title":"Nested Grids for Complex Layouts","duration":"25 min"}]},{"title":"Responsive Typography and Spacing","slug":"responsive-typography-and-spacing","description":"Learn how to make text and spacing adaptable to different screens.","lessons":[{"title":"Using Relative Units (em, rem, %)","duration":"15 min"},{"title":"Setting Responsive Font Sizes","duration":"18 min"},{"title":"Consistent Spacing with Margins and Padding","duration":"12 min"}]},{"title":"Project: Build a Responsive Blog Website","slug":"project","description":"Apply all concepts by building a fully responsive blog.","lessons":[{"title":"Setting Up the Project","duration":"20 min"},{"title":"Creating the Blog Homepage","duration":"25 min"},{"title":"Building a Responsive Header and Footer","duration":"30 min"},{"title":"Adding Blog Posts with Responsive Media","duration":"22 min"},{"title":"Using Media Queries to Optimize Layouts","duration":"28 min"},{"title":"Testing and Debugging Responsiveness","duration":"15 min"},{"title":"Deploying the Blog Online","duration":"20 min"}]}],"totalLessons":35,"duration":"8-10 hours","prerequisites":"Basic knowledge of HTML and CSS.","learningOutcomes":["Understand the principles of responsive web design.","Use media queries effectively to create adaptive layouts.","Build flexible layouts using CSS Flexbox and Grid.","Optimize images, media, and typography for multiple devices.","Develop and deploy a fully responsive blog website."]}')}}]);